<%@LANGUAGE="VBSCRIPT"%><!--#include file="Connections/test.asp" --><%set final_db = Server.CreateObject("ADODB.Recordset")final_db.ActiveConnection = MM_test_STRINGfinal_db.Source = "SELECT * FROM coupon"final_db.CursorType = 0final_db.CursorLocation = 2final_db.LockType = 3final_db.Open()final_db_numRows = 0%><%Dim Repeat1__numRowsRepeat1__numRows = -1Dim Repeat1__indexRepeat1__index = 0final_db_numRows = final_db_numRows + Repeat1__numRows%><html><head><title>Untitled Document</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"></head><body bgcolor="#FFFFFF" text="#000000"><form name="DeleteChecked" method="post" action="dodelete.asp">  <table border="0" cellspacing="2" cellpadding="0" wCopon_nameth="300"> <tr>    <td>Delete?</td>   <td>Coupon_price</td> </tr> <% While ((Repeat1__numRows <> 0) AND (NOT final_db.EOF)) %> <tr>    <td>   <input type="checkbox" name="del" value="<%=(final_db.Fields.Item("Copon_name").Value)%>">   </td>   <td><%=(final_db.Fields.Item("Coupon_price").Value)%></td> </tr> <%   Repeat1__index=Repeat1__index+1  Repeat1__numRows=Repeat1__numRows-1  final_db.MoveNext()Wend%> <tr>    <td>&nbsp;</td>   <td>   <input type="submit" value="Delete"> 'Ensure that your button DOES NOT have a NAME property, as this would be passed to the delete page too and ruin things!   </td> </tr>  </table></form></body></html><%final_db.Close()%>